export interface ElementInfo {
    /** 标签名（小写） */
    tagName: string;
    /** 类名 */
    className: string;
    /** ID */
    id: string;
    /** 文本内容（前100字符） */
    textContent: string;
    /** 所有HTML属性 */
    attributes: Record<string, string>;
    /** XPath路径 */
    xpath: string;
    /** CSS选择器 */
    cssSelector: string;
    /** 源码映射信息（可选） */
    sourceInfo?: SourceInfo;
}
export interface SourceInfo {
    /** 源码文件路径 */
    file: string;
    /** 行号 */
    line: number;
    /** 列号 */
    column: number;
    /** 组件名 */
    component: string;
}
export interface ElementRect {
    /** 元素左边距 */
    x: number;
    /** 元素上边距 */
    y: number;
    /** 元素宽度 */
    width: number;
    /** 元素高度 */
    height: number;
}
export interface ElementSelectedMessage {
    type: 'ELEMENT_SELECTED';
    data: ElementInfo;
    elementRect: ElementRect;
}
export interface InspectionControlMessage {
    type: 'START_INSPECTION' | 'STOP_INSPECTION' | 'CLEAR_HIGHLIGHT';
}
export type DOMInspectorMessage = ElementSelectedMessage | InspectionControlMessage;
export interface DOMInspectorAPI {
    /** 开始审查模式 */
    startInspection: () => void;
    /** 停止审查模式 */
    stopInspection: () => void;
    /** 清除高亮 */
    clearHighlight: () => void;
    /** 监听元素选择事件 */
    onElementSelected: (callback: (elementInfo: ElementInfo) => void) => () => void;
}
export interface ViteAdapter {
    name: string;
    apply: 'serve' | 'build';
    transformIndexHtml: {
        order: 'pre' | 'post';
        handler: (html: string) => string;
    };
}
export interface IcePluginAPI {
    onGetConfig: (callback: (config: any) => void) => void;
    onHook?: (hook: string, callback: (...args: any[]) => void) => void;
    context?: any;
}
export interface IcePlugin {
    name: string;
    setup: (api: IcePluginAPI) => void;
    runtime?: string;
}
export type ViteAdapterFactory = () => ViteAdapter;
export type IcePluginFactory = () => IcePlugin;
export type InjectFunction = () => void;
export type InjectHTMLFunction = (html: string) => string;
