export interface SourceMappingOptions {
    /** 是否启用源码映射注入 */
    enabled?: boolean;
    /** 自定义组件名提取函数 */
    getComponentName?: (fileName: string) => string;
    /** 需要注入源码映射的文件扩展名 */
    includeExtensions?: string[];
    /** 排除的文件路径模式 */
    excludePatterns?: string[];
    /** 是否在开发模式下启用 */
    devOnly?: boolean;
}
export interface SourceMappingInfo {
    /** 源码文件路径 */
    file: string;
    /** 行号 */
    line: number;
    /** 列号 */
    column: number;
    /** 组件名 */
    component: string;
}
export interface ViteAdapter {
    name: string;
    apply: 'serve' | 'build';
    enforce?: 'pre' | 'post';
    transform?: (code: string, id: string) => string | null;
}
export interface IcePluginAPI {
    onGetConfig: (callback: (config: any) => void) => void;
    onHook?: (hook: string, callback: (...args: any[]) => void) => void;
    context?: any;
}
export interface IcePlugin {
    name: string;
    setup: (api: IcePluginAPI) => void;
    runtime?: string;
}
export type ViteAdapterFactory = (options?: SourceMappingOptions) => ViteAdapter;
export type IcePluginFactory = (options?: SourceMappingOptions) => IcePlugin;
export type TransformFunction = (code: string, fileName: string, options?: SourceMappingOptions) => string;
